import * as React from 'react';
import Box from '@mui/material/Box';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import StepContent from '@mui/material/StepContent';
import Button from '@mui/material/Button';
import Paper from '@mui/material/Paper';
import Typography from '@mui/material/Typography';
import { styled } from '@mui/styles';
import { StepIcon } from '@mui/material';

const MyStepLabel = styled(StepLabel)({
  color: '#C4C4C4',
  fontSize: '1.5em',
  '& .Mui-active': {color: "#7151F5"},
  '& .Mui-disabled': {color: "#C4C4C4"},
  '& .Mui-completed': {color: "#7151F5"},
});

const MyStepIcon = styled(StepIcon)({
  color: '#C4C4C4',
  fontSize: '1.5em',
  '& .Mui-active': {color: "#7151F5"},
  '& .Mui-disabled': {color: "#C4C4C4"},
  '& .Mui-completed': {color: "#7151F5"},
});

const steps = [
  {
    label: 'Part I of the Solenoid Story Begins',
    description: `Once a Solenoid is born (minted), their journey creating energy throughout the Metaverse begins`,
  },
  {
    label: 'Free Airdrops',
    description:
      'Reserved Solenoids will be airdropped to randomly selected holders. This collection included the rarest 1 of 1 Solenoids',
  },
  {
    label: 'Distribution of 10 SOL',
    description: `To original minters who continue to hold Solenoids`,
  },
  {
    label: 'Soledroids Release',
    description: `Are they friend or foe? That’s for the community to decide. The Soledroids represent a new set of characters that will be introduced within the Solenoid Metaverse. This will be a new drop where Solenoid holders will have priority. `,
  },
  {
    label: 'Distribution of 10 SOL',
    description: `To original minters who continue to hold Solenoids`,
  },
  {
    label: 'Distribution of 10 SOL',
    description: `To original minters who continue to hold Solenoids`,
  },
];

function VerticalLinearStepper() {
  const [activeStep, setActiveStep] = React.useState(0);

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  return (
    <Box>
      <Stepper activeStep={activeStep} orientation="vertical">
        {steps.map((step, index) => (
          <Step key={step.label} expanded>
            <MyStepLabel
              StepIconComponent={MyStepIcon}
              icon=" "
            >
              <Typography fontSize="1.5em">{step.label}</Typography>
            </MyStepLabel>
            <StepContent>
              <Typography>{step.description}</Typography>
              <Box sx={{ mb: 2 }}>

              </Box>
            </StepContent>
          </Step>
        ))}
      </Stepper>
      {activeStep === steps.length && (
        <Paper square elevation={0} sx={{ p: 3 }}>
          <Typography>All steps completed - you&apos;re finished</Typography>
          <Button onClick={handleReset} sx={{ mt: 1, mr: 1 }}>
            Reset
          </Button>
        </Paper>
      )}
    </Box>
  );
}

export default VerticalLinearStepper;

