import { useEffect, useState } from "react";
import styled from "styled-components";
import Countdown from "react-countdown";
import Breakpoint, { BreakpointProvider, setDefaultBreakpoints } from "react-socks";
import { HashLink } from 'react-router-hash-link';
import { Link } from '@reach/router';

import * as anchor from "@project-serum/anchor";

import { LAMPORTS_PER_SOL } from "@solana/web3.js";

import { useAnchorWallet } from "@solana/wallet-adapter-react";
import { WalletDialogButton } from "@solana/wallet-adapter-material-ui";

import {
  CandyMachine,
  awaitTransactionSignatureConfirmation,
  getCandyMachineState,
  mintOneToken,
  shortenAddress,
} from "../candy-machine";

import React from 'react';
import Particle from './components/Particle';
import SliderMainParticle from './components/SliderMainParticle';
import CarouselNew from './components/CarouselNew';
import Footer from './components/footer';
import { createGlobalStyle } from 'styled-components';

import CustomizedAccordions from "./components/CustomAccordion";

import Button from '@mui/material/Button';
import VerticalLinearStepper from "./components/VerticalLinearStepper";
import SolenoidStory from "./components/SolenoidStory";


setDefaultBreakpoints([
  { xs: 0 },
  { l: 1199 },
  { xl: 1200 }
]);

const Header = function() {

  const [showmenu, btn_icon] = useState(false);
  const [openMenu, setOpenMenu] = React.useState(false);

  const closeMenu = (): void => {
    setOpenMenu(false);
  };


  useEffect(() => {
    const header = document.getElementById("myHeader");
    const totop = document.getElementById("scroll-to-top");
    const sticky = header.offsetTop;
    const scrollCallBack = window.addEventListener("scroll", () => {
        btn_icon(false);
        if (window.pageYOffset > sticky) {
          header.classList.add("sticky");
          totop.classList.add("show");

        } else {
          header.classList.remove("sticky");
          totop.classList.remove("show");
        } if (window.pageYOffset > sticky) {
          closeMenu();
        }
      });
      return () => {
        window.removeEventListener("scroll", scrollCallBack);
      };
    }, []);


    return (
    <header id="myHeader" className='navbar white'>
     <div className='container'>
       <div className='row w-100-nav'>
          <div className='logo px-0'>
              <div className='navbar-title navbar-item'>
                  <Link to="/">
                  <img
                    src="./img/logo-light.png"
                    className="img-fluid d-none"
                    alt="#"
                  />
                </Link>
              </div>
          </div>

              <BreakpointProvider>
                <Breakpoint l down>
                {showmenu &&
                  <div className='menu'>
                    <div className='navbar-item'>
                        <div >
                          <div className="dropdown-custom dropdown-toggle btn" >

                            <a onClick={() => btn_icon(!showmenu)}>Story</a>
                            <span className='lines'></span>                         
                          </div>

                        </div>
                    </div>
                    <div className='navbar-item'>
                      <div>
                          <div className="dropdown-custom dropdown-toggle btn" >
                          <a onClick={() => btn_icon(!showmenu)}>Roadmap</a>
                            <span className='lines'></span>

                          </div>

                        </div>
                    </div>
                    <div className='navbar-item'>
                      <div>
                          <div className="dropdown-custom dropdown-toggle btn" >
                          <HashLink to="/#story" onClick={() => btn_icon(!showmenu)}>FAQ</HashLink>
                            <span className='lines'></span>

                          </div>
                        </div>
                    </div>

                  </div>}
                </Breakpoint>


                <Breakpoint xl>
                  <div className='menu'>
                    <div className='navbar-item'>
                        <div >
                          <div className="dropdown-custom dropdown-toggle btn">

                            Story
                            <span className='lines'></span>                         
                          </div>

                        </div>
                    </div>
                    <div className='navbar-item'>
                      <div>
                          <div className="dropdown-custom dropdown-toggle btn" >
                            Roadmap
                            <span className='lines'></span>

                          </div>

                        </div>
                    </div>
                    <div className='navbar-item'>
                      <div>
                          <div className="dropdown-custom dropdown-toggle btn" >
                            FAQ
                            <span className='lines'></span>

                          </div>
                        </div>
                    </div>

                  </div>
                </Breakpoint>
              </BreakpointProvider>

              <div className='mainside'>
                <Link to="/wallet" className="btn-main">Connect Wallet</Link>
              </div>

      </div>

        <button className="nav-icon" onClick={() => btn_icon(!showmenu)}>
          <div className="menu-line white"></div>
          <div className="menu-line1 white"></div>
          <div className="menu-line2 white"></div>
        </button>

      </div>     
    </header>
    );
}

const ConnectButton = styled(WalletDialogButton)``;

const CounterText = styled.span``; // add your styles here

const MintContainer = styled.div``; // add your styles here

const MintButton = styled(Button)``; // add your styles here

export interface HomeProps {
  candyMachineId: anchor.web3.PublicKey;
  config: anchor.web3.PublicKey;
  connection: anchor.web3.Connection;
  startDate: number;
  treasury: anchor.web3.PublicKey;
  txTimeout: number;
}

const Home = (props: HomeProps) => {
  const [balance, setBalance] = useState<number>();
  const [isActive, setIsActive] = useState(false); // true when countdown completes
  const [isSoldOut, setIsSoldOut] = useState(false); // true when items remaining is zero
  const [isMinting, setIsMinting] = useState(false); // true when user got to press MINT

  const [itemsAvailable, setItemsAvailable] = useState(0);
  const [itemsRedeemed, setItemsRedeemed] = useState(0);
  const [itemsRemaining, setItemsRemaining] = useState(0);

  const [alertState, setAlertState] = useState<AlertState>({
    open: false,
    message: "",
    severity: undefined,
  });

  const [startDate, setStartDate] = useState(new Date(props.startDate));

  const wallet = useAnchorWallet();
  const [candyMachine, setCandyMachine] = useState<CandyMachine>();

  const refreshCandyMachineState = () => {
    (async () => {
      if (!wallet) return;

      const {
        candyMachine,
        goLiveDate,
        itemsAvailable,
        itemsRemaining,
        itemsRedeemed,
      } = await getCandyMachineState(
        wallet as anchor.Wallet,
        props.candyMachineId,
        props.connection
      );

      setItemsAvailable(itemsAvailable);
      setItemsRemaining(itemsRemaining);
      setItemsRedeemed(itemsRedeemed);

      setIsSoldOut(itemsRemaining === 0);
      setStartDate(goLiveDate);
      setCandyMachine(candyMachine);
    })();
  };

  const onMint = async () => {
    try {
      setIsMinting(true);
      if (wallet && candyMachine?.program) {
        const mintTxId = await mintOneToken(
          candyMachine,
          props.config,
          wallet.publicKey,
          props.treasury
        );

        const status = await awaitTransactionSignatureConfirmation(
          mintTxId,
          props.txTimeout,
          props.connection,
          "singleGossip",
          false
        );

        if (!status?.err) {
          setAlertState({
            open: true,
            message: "Congratulations! Mint succeeded!",
            severity: "success",
          });
        } else {
          setAlertState({
            open: true,
            message: "Mint failed! Please try again!",
            severity: "error",
          });
        }
      }
    } catch (error: any) {
      // TODO: blech:
      let message = error.msg || "Minting failed! Please try again!";
      if (!error.msg) {
        if (error.message.indexOf("0x138")) {
        } else if (error.message.indexOf("0x137")) {
          message = `SOLD OUT!`;
        } else if (error.message.indexOf("0x135")) {
          message = `Insufficient funds to mint. Please fund your wallet.`;
        }
      } else {
        if (error.code === 311) {
          message = `SOLD OUT!`;
          setIsSoldOut(true);
        } else if (error.code === 312) {
          message = `Minting period hasn't started yet.`;
        }
      }

      setAlertState({
        open: true,
        message,
        severity: "error",
      });
    } finally {
      if (wallet) {
        const balance = await props.connection.getBalance(wallet.publicKey);
        setBalance(balance / LAMPORTS_PER_SOL);
      }
      setIsMinting(false);
      refreshCandyMachineState();
    }
  };

  useEffect(() => {
    (async () => {
      if (wallet) {
        const balance = await props.connection.getBalance(wallet.publicKey);
        setBalance(balance / LAMPORTS_PER_SOL);
      }
    })();
  }, [wallet, props.connection]);

  useEffect(refreshCandyMachineState, [
    wallet,
    props.candyMachineId,
    props.connection,
  ]);

const GlobalStyles = createGlobalStyle`
  header#myHeader .logo .d-block{
    display: none !important;
  }
  h1{
    color: #843BE6;
    margin-bottom: 20px;
  }
  header#myHeader .logo .d-none{
    display: block !important;
  }
  .navbar .mainside a{
    background: #7251F5;
    &:hover{
      box-shadow: 2px 2px 20px 0px #7251F5;
    }
  }
  .item-dropdown{
    .dropdown{
      a{
        &:hover{
          background: #7251F5;
        }
      }
    }
  }
  .btn-main{
    background: #7251F5;
    &:hover{
      box-shadow: 2px 2px 20px 0px #7251F5;
    }
  }
  p.lead{
    color: #a2a2a2;
  }
  .navbar .navbar-item .lines{
    border-bottom: 2px solid #7251F5;
  }
  .jumbotron.no-bg{
    height: 100vh;
    overflow: hidden;
    background-repeat: repeat;
    background-size: cover;
    background-position: bottom;
    background-repeat: no-repeat;
  }
  #tsparticles{
    top: 0;
  }
  .text-uppercase.color{
    color: #7251F5;
  }
  .de_count h3 {
    font-size: 36px;
    margin-bottom: 0px;
  }
  .de_count h5{
    font-size: 14px;
    font-weight: 500;
  }
  h2 {
    font-size: 30px;
  }
  .box-url{
    text-align: center;
    h4{
      font-size: 16px;
    }
  }
  .de_countdown{
    border: solid 2px #7251F5;
  }
  .author_list_pp, .author_list_pp i, 
  .nft_coll_pp i, .feature-box.style-3 i, 
  footer.footer-light #form_subscribe #btn-subscribe i, 
  #scroll-to-top div{
    background: #7251F5;
  }
  footer.footer-light .subfooter .social-icons span i{
    background: #403f83;
  }
  .author_list_pp:hover img{
    box-shadow: 0px 0px 0px 2px #7251F5;
  }
  .nft__item_action span{
    color: #7251F5;
  }
  .feature-box.style-3 i.wm{
    color: rgba(131,100,226, .1);
  }
  @media only screen and (max-width: 1199px) {
    .navbar{

    }
    .navbar .menu-line, .navbar .menu-line1, .navbar .menu-line2{
      background: #fff;
    }
    .item-dropdown .dropdown a{
      color: #fff !important;
    }
  }

  header#myHeader.navbar.white {
    background: #212428;
  }
`;

// Random component
const Completionist = () => <span>You are good to go!</span>;

// Renderer callback with condition
const renderer = ({ days, hours, minutes, seconds, completed }) => {
  if (completed) {
    // Render a completed state
    return <Completionist />;
  } else {
    // Render a countdown
    return (

        <div className="container-fluid mt-5">
          <div className="centerthis">
          <div></div>
          <div id="clockdiv">           

            <div>
              <div className="number">{days}</div>
              <div className="smalltext">Days</div>
            </div>

            <div className="colon">:</div>

            <div>
              <div className="number">{hours}</div>              
              <div className="smalltext">Hours</div>
            </div> 

            <div className="colon middle">:</div>

            <div>
              <div className="number">{minutes}</div>
              <div className="smalltext">Minutes</div>
            </div> 

            <div className="colon">:</div>

            <div>
              <div className="number">{seconds}</div>
              <div className="smalltext">Seconds</div>
            </div>

          </div>
          <div></div>
          </div>
        </div>

    );

  }
}

  return (  


    <main>

<div>
  <Header />
  <GlobalStyles />
      <section className="container" >

         <SliderMainParticle/>
      </section>

      <section className="container">
      <h1 className='text-center'>Time Till Launch</h1>
      <div className='row'>

          <div className='col-lg-12 mb-2 justify-content-center'>
          <Countdown
            date={Date.now() + 500000}
            renderer={renderer}
          />
          </div>
        </div> 


      </section>

      <section className='container no-top no-bottom'>
        <div className='row'>

          <div className='col-lg-12 mb-2'>

          </div>
        </div> 
        <CarouselNew/>
      </section>

   <section className="container" id="story">

      <SolenoidStory/>
   </section>

  <section className='container'>
  <h1>Roadmap</h1>
    <div className='row'>

      <div className='col-md-12'>
        <VerticalLinearStepper />
      </div>
    </div>
  </section>

  <section className='container'>
  <h1>FAQ</h1>
    <div className='row'>

      <div className='col-md-12'>
        <CustomizedAccordions />
      </div>
    </div>
  </section>

    <Footer />

  </div>

      {/* {wallet && (
        <p>Wallet {shortenAddress(wallet.publicKey.toBase58() || "")}</p>
      )}

      {wallet && <p>Balance: {(balance || 0).toLocaleString()} SOL</p>}

      {wallet && <p>Total Available: {itemsAvailable}</p>}

      {wallet && <p>Redeemed: {itemsRedeemed}</p>}

      {wallet && <p>Remaining: {itemsRemaining}</p>}

      <MintContainer>
        {!wallet ? (
          <ConnectButton>Connect Wallet</ConnectButton>
        ) : (
          <MintButton
            disabled={isSoldOut || isMinting || !isActive}
            onClick={onMint}
            variant="contained"
          >
            {isSoldOut ? (
              "SOLD OUT"
            ) : isActive ? (
              isMinting ? (
                <CircularProgress />
              ) : (
                "MINT"
              )
            ) : (
              <Countdown
                date={startDate}
                onMount={({ completed }) => completed && setIsActive(true)}
                onComplete={() => setIsActive(true)}
                renderer={renderCounter}
              />
            )}
          </MintButton>
        )}
      </MintContainer>

      <Snackbar
        open={alertState.open}
        autoHideDuration={6000}
        onClose={() => setAlertState({ ...alertState, open: false })}
      >
        <Alert
          onClose={() => setAlertState({ ...alertState, open: false })}
          severity={alertState.severity}
        >
          {alertState.message}
        </Alert>
      </Snackbar> */}
    </main>
  );
};

interface AlertState {
  open: boolean;
  message: string;
  severity: "success" | "info" | "warning" | "error" | undefined;
}

const renderCounter = ({ days, hours, minutes, seconds, completed }: any) => {
  return (
    <CounterText>
      {hours + (days || 0) * 24} hours, {minutes} minutes, {seconds} seconds
    </CounterText>
  );
};  

export default Home;
